CREATE OR REPLACE FUNCTION obter_desc_cliente (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_cliente_w	varchar(255);


BEGIN

select	SUBSTR(obter_dados_pf_pj(null,cd_cnpj,ie_opcao_p),1,255)
