CREATE OR REPLACE FUNCTION obter_desc_coluna_fluxo_agrup ( nr_seq_agrupador_p bigint, nr_coluna_p bigint) RETURNS varchar AS $body$
DECLARE


ds_coluna_w	fluxo_caixa_agrup_coluna.ds_coluna%type;
qt_cont_w	bigint := 1;

C01 CURSOR FOR
SELECT	a.ds_coluna
from	fluxo_caixa_agrup_coluna a
where	a.nr_seq_agrupador = nr_seq_agrupador_p
order by nr_ordem_apres;


BEGIN

open C01;
loop
fetch C01 into
	ds_coluna_w;
EXIT WHEN NOT FOUND or qt_cont_w = nr_coluna_p;  /* apply on C01 */
	begin
	qt_cont_w	:= qt_cont_w + 1;
	end;
end loop;
close C01;

return	ds_coluna_w;

end;
$body$
