SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_completa_req_prod (nr_seq_requisito_p bigint) RETURNS varchar AS $body$
DECLARE


ds_title_prod_req_w		reg_product_requirement.ds_title%type;
nr_customer_requirement_w	reg_product_requirement.nr_customer_requirement%type;
ds_title_cust_req_w		reg_customer_requirement.ds_title%type;
nr_seq_features_w		reg_customer_requirement.nr_seq_features%type;
ds_feature_w			reg_features_customer.ds_feature%type;
nr_seq_area_customer_w		reg_features_customer.nr_seq_area_customer%type;
ds_area_w			reg_area_customer.ds_area%type;

ds_retorno_w			varchar(2000);


BEGIN

select	max(ds_title),
	max(nr_customer_requirement)
into STRICT	ds_title_prod_req_w,
	nr_customer_requirement_w
from	reg_product_requirement
where	nr_sequencia = nr_seq_requisito_p;

select	max(ds_title),
	max(nr_seq_features)
into STRICT	ds_title_cust_req_w,
	nr_seq_features_w
from	reg_customer_requirement
where	nr_sequencia = nr_customer_requirement_w;

select 	max(ds_feature),
	max(nr_seq_area_customer)
into STRICT	ds_feature_w,
	nr_seq_area_customer_w
from 	reg_features_customer
where	nr_sequencia = nr_seq_features_w;

select	max(ds_area)
into STRICT	ds_area_w
from	reg_area_customer
where	nr_sequencia	= nr_seq_area_customer_w;

ds_retorno_w := substr(ds_area_w ||' > '|| ds_feature_w ||' > '|| ds_title_cust_req_w ||' > '|| ds_title_prod_req_w,1,2000);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
