CREATE OR REPLACE FUNCTION obter_desc_compl_unidade ( nr_seq_unidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_setor_atendimento_w		varchar(80);
cd_unidade_basica_w		varchar(10);
cd_unidade_compl_w		varchar(10);


BEGIN

select	substr(obter_nome_setor(cd_setor_atendimento),1,80),
	cd_unidade_basica,
	cd_unidade_compl
into STRICT	ds_setor_atendimento_w,
	cd_unidade_basica_w,
	cd_unidade_compl_w
from	unidade_atendimento
where	nr_seq_interno		= nr_seq_unidade_p;

return wheb_mensagem_pck.get_texto(796345) || ' ' || ds_setor_atendimento_w || ', '|| wheb_mensagem_pck.get_texto(796346) ||': ' || cd_unidade_basica_w || ', '|| wheb_mensagem_pck.get_texto(796347) ||': ' || cd_unidade_compl_w;

end;
