CREATE OR REPLACE FUNCTION obter_desc_controle_pad ( nr_controle_pad_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);


BEGIN

select a.ds_pad
into STRICT ds_retorno_w
from hc_pad_cad a, hc_pad_controle b
where a.nr_sequencia = b.nr_seq_hc_pad_cad
and b.nr_sequencia = nr_controle_pad_p;

return	ds_retorno_w;

end;
