CREATE OR REPLACE FUNCTION obter_desc_cpoe_diluicao_md ( ie_ref_calculo_p text, qt_solucao_total_p bigint, qt_sol_tot_format_p bigint, qt_tempo_aplicacao_p bigint, ds_desc_diluicao_p text default null) RETURNS varchar AS $body$
