CREATE OR REPLACE FUNCTION obter_desc_curta_proc_rotina ( nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


   ds_procedimento_curta_w	pep_procedimento_rotina.ds_descricao_curta%type;


BEGIN

	select max(pr.ds_descricao_curta) 
	into STRICT   ds_procedimento_curta_w 
	from   pep_procedimento_rotina pr                                        
	      ,pep_proc_rotina_item pri                                          
	where  pri.nr_seq_rotina = pr.nr_sequencia                               
	and (pri.nr_seq_proc_interno = nr_seq_proc_interno_p                   
	or     pri.cd_procedimento     = cd_procedimento_p)                      
	and	   pri.ie_origem_proced       = ie_origem_proced_p;

   return	ds_procedimento_curta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
