CREATE OR REPLACE FUNCTION obter_desc_curta_setor ( CD_SETOR_ATENDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


nm_curto_w	varchar(10);


BEGIN


if (CD_SETOR_ATENDIMENTO_P IS NOT NULL AND CD_SETOR_ATENDIMENTO_P::text <> '') then
	begin
	select	nm_curto
	into STRICT	nm_curto_w
	from	setor_atendimento
	where	cd_setor_atendimento = CD_SETOR_ATENDIMENTO_P;
	exception
		 when others then
		 nm_curto_w := ' ';
	end;

end if;
return nm_curto_w;
END;
