CREATE OR REPLACE FUNCTION obter_desc_dados_cateter (nr_seq_cateter_p bigint, nr_seq_tipo_cateter_p bigint, nr_seq_calibre_cateter_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cateter_w		varchar(80);
ds_tipo_cateter_w	varchar(80);
ds_calibre_cateter_w	varchar(80);
ds_dados_cateter_w	varchar(240);


BEGIN

if (nr_seq_cateter_p IS NOT NULL AND nr_seq_cateter_p::text <> '') then
	select	ds_cateter
	into STRICT	ds_cateter_w
	from	hem_cateter_guia
	where	nr_sequencia = nr_seq_cateter_p;
end if;
if (nr_seq_tipo_cateter_p IS NOT NULL AND nr_seq_tipo_cateter_p::text <> '') then
	select	ds_tipo_cateter
	into STRICT	ds_tipo_cateter_w
	from	hem_cateter_guia_tipo
	where	nr_sequencia = nr_seq_tipo_cateter_p;
end if;

if (nr_seq_calibre_cateter_p IS NOT NULL AND nr_seq_calibre_cateter_p::text <> '') then
	select	ds_calibre_cateter
	into STRICT	ds_calibre_cateter_w
	from	hem_cateter_guia_calibre
	where	nr_sequencia = nr_seq_calibre_cateter_p;
end if;

ds_dados_cateter_w	:= ds_cateter_w || ' ' || ds_tipo_cateter_w || ' ' || ds_calibre_cateter_w;

RETURN	ds_dados_cateter_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
