CREATE OR REPLACE FUNCTION obter_desc_dados_corda (nr_seq_corda_p bigint, nr_seq_tipo_corda_p bigint, nr_seq_calibre_corda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_corda_w		varchar(80);
ds_tipo_corda_w		varchar(80);
ds_calibre_corda_w	varchar(80);
ds_dados_corda_w	varchar(240);


BEGIN

if (nr_seq_corda_p IS NOT NULL AND nr_seq_corda_p::text <> '') then
	select	ds_corda
	into STRICT	ds_corda_w
	from	hem_corda_guia
	where	nr_sequencia = nr_seq_corda_p;
end if;
if (nr_seq_tipo_corda_p IS NOT NULL AND nr_seq_tipo_corda_p::text <> '') then
	select	ds_tipo_corda_guia
	into STRICT	ds_tipo_corda_w
	from	hem_corda_guia_tipo
	where	nr_sequencia = nr_seq_tipo_corda_p;
end if;

if (nr_seq_calibre_corda_p IS NOT NULL AND nr_seq_calibre_corda_p::text <> '') then
	select	ds_calibre_corda_guia
	into STRICT	ds_calibre_corda_w
	from	hem_corda_guia_calibre
	where	nr_sequencia = nr_seq_calibre_corda_p;
end if;

ds_dados_corda_w	:= ds_corda_w || ' ' || ds_tipo_corda_w || ' ' || ds_calibre_corda_w;

RETURN	ds_dados_corda_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
