CREATE OR REPLACE FUNCTION obter_desc_dado_clinico (nr_sequencia_p bigint, ie_dado_clinico_p text) RETURNS varchar AS $body$
DECLARE


ds_dado_retorno_w	varchar(80);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (coalesce(ie_dado_clinico_p, 'D') = 'D') then
		select	ds_dado_clinico
		into STRICT	ds_dado_retorno_w
		from	hem_dado_clinico
		where	nr_sequencia = nr_sequencia_p;
	elsif (coalesce(ie_dado_clinico_p, 'D') = 'M') then
		select	ds_medic
		into STRICT	ds_dado_retorno_w
		from	hem_medic
		where	nr_sequencia = nr_sequencia_p;
	elsif (coalesce(ie_dado_clinico_p, 'D') = 'E') then
		select	ds_exame
		into STRICT	ds_dado_retorno_w
		from	hem_exame
		where	nr_sequencia = nr_sequencia_p;
	end if;
end if;

RETURN	ds_dado_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
