CREATE OR REPLACE FUNCTION obter_desc_dcb_mat (cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C - Código
D - Descrição
*/
ds_retorno_w	varchar(80);
nr_seq_dcb_w	bigint;
ds_dcb_w	varchar(80);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	select	max(nr_seq_dcb),
		substr(obter_desc_dcb(max(nr_seq_dcb)),1,80) ds_dcb
	into STRICT	nr_seq_dcb_w,
		ds_dcb_w
	from	material_dcb
	where	cd_material = cd_material_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := nr_seq_dcb_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_dcb_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
