CREATE OR REPLACE FUNCTION obter_desc_declaracao_conv ( cd_convenio_p bigint, ie_status_acerto_p text, cd_categoria_p text) RETURNS varchar AS $body$
DECLARE


ds_declaracao_w		varchar(2000);
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
	SELECT 	ds_declaracao
	from   	convenio_declaracao_rel
	where  	cd_convenio = cd_convenio_p
	and	coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and    	((ie_status_conta = 'A') or (ie_status_conta = ie_status_acerto_p))
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0)
	order by
		coalesce(cd_estabelecimento,0),
		coalesce(cd_categoria,'0');

BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

open C01;
loop
fetch C01 into
	ds_declaracao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_declaracao_w	:= ds_declaracao_w;
	end;
end loop;
close C01;

return	ds_declaracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
