CREATE OR REPLACE FUNCTION obter_desc_desfecho_html ( ie_desfecho_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_desfecho_w		varchar(254) := null;

BEGIN


 ---  Criado o parâmetro ie_tipo_p, caso queira utilizar a funciton para buscar de outro desfecho que não seja o de consulta.
if (ie_desfecho_p IS NOT NULL AND ie_desfecho_p::text <> '')  and (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '') then
	begin

	if (ie_tipo_p = 'C') then

		if (ie_desfecho_p = 'A') then

			ds_desfecho_w := Wheb_mensagem_pck.get_texto(796957); --'Agendar consulta';
		elsif (ie_desfecho_p = 'L') then

			ds_desfecho_w := Wheb_mensagem_pck.get_texto(796958); --'Liberar cidadão';
		elsif (ie_desfecho_p = 'E') then

			ds_desfecho_w := Wheb_mensagem_pck.get_texto(796959); --'Encaminhar para especialista';
		end if;
	end if;

		end;
end if;

return ds_desfecho_w;

END;
