CREATE OR REPLACE FUNCTION obter_desc_desfecho_pcr ( ie_desfecho_p text) RETURNS varchar AS $body$
DECLARE


ds_desfecho_w		varchar(254) := null;

BEGIN

if (ie_desfecho_p IS NOT NULL AND ie_desfecho_p::text <> '') then
	begin

	if (ie_desfecho_p = 'R') then

		ds_desfecho_w := Wheb_mensagem_pck.get_texto(308958); --'Reversão da parada';
	elsif (ie_desfecho_p = 'O') then

		ds_desfecho_w := Wheb_mensagem_pck.get_texto(308959); --'Óbito';
	end if;

	end;
end if;

return ds_desfecho_w;

END;
