SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_diagnostico_ger (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_diagnostico_w 				diagnostico_doenca.ds_diagnostico%type;
ie_diagnostico_estruturado_w	varchar(1) := 'N';


BEGIN

if ( nr_atendimento_p > 0 ) then

	select	coalesce(max('S'),'N' )
	into STRICT 	ie_diagnostico_estruturado_w
	from	diagnostico_medico a,
			diagnostico_doenca b
	where	a.dt_diagnostico = b.dt_diagnostico
	and		a.nr_atendimento = b.nr_atendimento
	and		a.nr_atendimento = nr_atendimento_p;

	if ( ie_diagnostico_estruturado_w = 'N' )then

		select	max(b.ds_diagnostico)
		into STRICT	ds_diagnostico_w
		from 	diagnostico_medico b
		where	(b.ds_diagnostico IS NOT NULL AND b.ds_diagnostico::text <> '') 
		and   	b.nr_atendimento = nr_atendimento_p;
		
		return	coalesce( ds_diagnostico_w, '');
		
	end if;

end if;

return	'';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_diagnostico_ger (nr_atendimento_p bigint) FROM PUBLIC;

