CREATE OR REPLACE FUNCTION obter_desc_diag_rotina (nr_seq_rotina_p bigint) RETURNS varchar AS $body$
DECLARE


cd_perfil_w		cid_rotina_regra.cd_perfil%type;
cd_estabelecimento_w	cid_rotina_regra.cd_estabelecimento%type;
ds_diagnostico_rotina_w	varchar(255);


BEGIN

cd_perfil_w 		:= wheb_usuario_pck.get_cd_perfil;
cd_estabelecimento_w 	:= wheb_usuario_pck.get_cd_estabelecimento;

select 	substr(max(a.ds_rotina), 1, 254)
into STRICT	ds_diagnostico_rotina_w
FROM cid_rotina a
LEFT OUTER JOIN cid_rotina_regra b ON (a.nr_sequencia = b.nr_seq_rotina)
WHERE a.nr_sequencia = nr_seq_rotina_p  and obter_se_cid_rotina_lib(a.nr_sequencia, cd_estabelecimento_w, cd_perfil_w) = 'S';

return ds_diagnostico_rotina_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
