CREATE OR REPLACE FUNCTION obter_desc_dias_prev_alta ( ie_previsao_p text, cd_especialidade_p text default 0) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
qt_dias_w		bigint;

BEGIN

if (ie_previsao_p IS NOT NULL AND ie_previsao_p::text <> '') then
	select	max(qt_dias)
	into STRICT	qt_dias_w
	from	REGRA_DIAS_PREV_ALTA
	where	ie_previsao = ie_previsao_p
	and		coalesce(cd_especialidade_regra,coalesce(cd_especialidade_p,0)) = coalesce(cd_especialidade_p,0);

	if (qt_dias_w IS NOT NULL AND qt_dias_w::text <> '') then
		ds_retorno_w	:= ' - ' || qt_dias_w || ' ' || obter_desc_expressao(303740)/*' dias previstos. '*/;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
