CREATE OR REPLACE FUNCTION obter_desc_diluicao_gpt (nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


nm_descricao_w		varchar(2000);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	begin

	select	ds_orientacao_preparo || ds_observacao
	into STRICT	nm_descricao_w
	from 	cpoe_material
	where 	nr_sequencia = nr_sequencia_p;

	exception
	when others then
		nm_descricao_w := '';
	end;

end if;

return	nm_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
