SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_disp_cons ( nr_seq_disp_pac_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_dispositivo_w	varchar(80);
ds_topografia_w		varchar(255);
ds_retorno_w		varchar(255);
				

BEGIN



select	substr(obter_descricao_padrao('DISPOSITIVO','DS_DISPOSITIVO',nr_seq_dispositivo),1,80),
	substr(obter_desc_topografia_dor(nr_seq_topografia),1,80) || CASE WHEN coalesce(ie_lado::text, '') = '' THEN ' '  ELSE ' '||obter_desc_expressao(292416) ||': ' END  || substr(obter_valor_dominio(1372,ie_lado),1,20)	
into STRICT	ds_dispositivo_w,
	ds_topografia_w
from	atend_pac_dispositivo
where	nr_sequencia	=	nr_seq_disp_pac_p;

ds_retorno_w	:= ds_dispositivo_w;
if (ds_topografia_w IS NOT NULL AND ds_topografia_w::text <> '') and (ds_topografia_w <> ' ') then
	ds_retorno_w := ds_retorno_w || ' ('|| ds_topografia_w ||')';
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
