CREATE OR REPLACE FUNCTION obter_desc_dose_embalagem (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	        varchar(100);
ds_dose_embalagem_w     varchar(15);
ds_unidade_medida_w     varchar(100);
ds_separador_w          varchar(100);


BEGIN
    ds_separador_w := 'Menge';

    select  max(ds_dose_embalagem),
			max(CD_UNID_MED_DOSE_EMB)
    into STRICT    ds_dose_embalagem_w,
            ds_unidade_medida_w
    from    material
    where   cd_material = cd_material_p;

    if (ds_dose_embalagem_w IS NOT NULL AND ds_dose_embalagem_w::text <> '' AND ds_unidade_medida_w IS NOT NULL AND ds_unidade_medida_w::text <> '')then
        ds_retorno_w := ds_dose_embalagem_w || ' ' || ds_separador_w || ' ' || ds_unidade_medida_w;
    else
        ds_retorno_w := ds_dose_embalagem_w || ds_unidade_medida_w;
    end if;

    return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
