CREATE OR REPLACE FUNCTION obter_desc_eis_indicador ( nr_seq_indicador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);


BEGIN

select	substr(max(obter_desc_expressao(CD_EXP_INDICADOR,DS_INDICADOR)),1,50)
into STRICT	ds_retorno_w
from	indicador_gestao
where	nr_sequencia	= nr_seq_indicador_p;

return	ds_retorno_w;

end;
