CREATE OR REPLACE FUNCTION obter_desc_equip_agenda (cd_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_equipamento_w	varchar(100);


BEGIN

select	SUBSTR(coalesce(ds_equip_curto,ds_equipamento),1,100)
into STRICT	ds_equipamento_w
from	equipamento
where	cd_equipamento	= cd_equipamento_p;

return	ds_equipamento_w;

end;
