CREATE OR REPLACE FUNCTION obter_desc_escala_ansi_depre ( qt_pontuacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(30);

/*
 ie_opcao
 1 - ansiedade
 2 - depressão
*/
BEGIN

if (ie_opcao_p = 1) then
    if	(qt_pontuacao_p >= 0 AND qt_pontuacao_p <=8) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308960); --'Sem ansiedade';
	elsif (qt_pontuacao_p >= 9 ) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308961); --'Com ansiedade';
	end if;
else
	if	(qt_pontuacao_p >= 0 AND qt_pontuacao_p <=8) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308962); --'Sem depressão';
	elsif (qt_pontuacao_p >= 9 ) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308963); --'Com depressão';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
