CREATE OR REPLACE FUNCTION obter_desc_escala_avei (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_escala_avei_w 	varchar(100);

BEGIN
	if (qt_pontuacao_p < 5) then
	             ds_escala_avei_w := Wheb_mensagem_pck.get_texto(308964); --'Déficit neurológico leve';
	elsif (qt_pontuacao_p >= 5 AND qt_pontuacao_p <= 13)then
	             ds_escala_avei_w := Wheb_mensagem_pck.get_texto(308965); --'Déficit neurológico moderado';
	elsif (qt_pontuacao_p > 13) then
	             ds_escala_avei_w := Wheb_mensagem_pck.get_texto(308966); --'Déficit neurológico grave';
	else
	             ds_escala_avei_w := '  ';
	end if;

return ds_escala_avei_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
