CREATE OR REPLACE FUNCTION obter_desc_escala_barthel (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_escala_barthel_w 	varchar(100);

BEGIN
	if (qt_pontuacao_p <= 50) then
	             ds_escala_barthel_w := Wheb_mensagem_pck.get_texto(308967); --'Paciente dependente';
