CREATE OR REPLACE FUNCTION obter_desc_escala_comfort_b (qt_pontuacao bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (qt_pontuacao <= 10) then
	ds_retorno_w := OBTER_DESC_EXPRESSAO(942109); -- Super sedado
elsif (qt_pontuacao > 10 and qt_pontuacao < 23) then
	ds_retorno_w := OBTER_DESC_EXPRESSAO(942105); -- Sedado
elsif (qt_pontuacao >= 23) then
	ds_retorno_w := OBTER_DESC_EXPRESSAO(942107); -- Pouco sedado
end if;

return	ds_retorno_w;

end;
