CREATE OR REPLACE FUNCTION obter_desc_escala_dor_inapto (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_escala_dor_w			varchar(5);
ds_resultado_w			varchar(150);
nr_sequencia_w			bigint;


BEGIN
select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	atendimento_sinal_vital
where	nr_atendimento = nr_atendimento_p
and		ie_situacao = 'A';

if (nr_sequencia_w > 0) then
	select	max(cd_escala_dor)
	into STRICT	cd_escala_dor_w
	from	atendimento_sinal_vital
	where	nr_sequencia = nr_sequencia_w;
else
	cd_escala_dor_w := null;
end if;


if (ie_opcao_p = 'DS') then
	if (cd_escala_dor_w = 'IA') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(307921); -- Inapto
	end if;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
