CREATE OR REPLACE FUNCTION obter_desc_escala_dor ( cd_escala_dor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (cd_escala_dor_p IS NOT NULL AND cd_escala_dor_p::text <> '') then

	ds_retorno_w	:= substr(obter_valor_dominio(1298,cd_escala_dor_p),1,60);

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	ds_escala_dor
		into STRICT	ds_retorno_w
		from	escala_dor
		where	cd_escala_dor	= cd_escala_dor_p;
	end if;


end if;

return	ds_retorno_w;

end;
$body$
