CREATE OR REPLACE FUNCTION obter_desc_escala_hamilton ( qt_score_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);




BEGIN


if (qt_score_p >=0 and qt_score_p <=7 ) then

	ds_retorno_w	:= wheb_mensagem_pck.get_texto(1070039); -- Normal
elsif (qt_score_p >=8 and qt_score_p <=13 ) then

	ds_retorno_w	:= wheb_mensagem_pck.get_texto(1070921); -- Mild Depression
elsif (qt_score_p >=14 and qt_score_p <=18 ) then

	ds_retorno_w	:= wheb_mensagem_pck.get_texto(1070922); -- Moderate Depression
elsif (qt_score_p >=19 and qt_score_p <=22 ) then

	ds_retorno_w	:= wheb_mensagem_pck.get_texto(1070923); --  Severe Depression
elsif (qt_score_p >=23  ) then

	ds_retorno_w	:= wheb_mensagem_pck.get_texto(1070924); -- Very Severe Depression 
end if;

return ds_retorno_w;


end;
