CREATE OR REPLACE FUNCTION obter_desc_escala_hunt (nr_grau_p text) RETURNS varchar AS $body$
DECLARE

ds_escala_hunt_w	 	varchar(100);

BEGIN
	 if ((upper(nr_grau_p) = 'GRAU 1') or (upper(nr_grau_p) = 'GRAU 2')) then
			ds_escala_hunt_w := obter_desc_expressao(781595);--'Paciente em boas condições pré-operatórias';
	 elsif ((upper(nr_grau_p) = 'GRAU 3') or (upper(nr_grau_p) = 'GRAU 4') or (upper(nr_grau_p) = 'GRAU 5')) then
	    	ds_escala_hunt_w := obter_desc_expressao(781597);--'Paciente em condições pré-operatórias inadequadas';
	 else
	    	ds_escala_hunt_w := ' ';
	 end if;

return ds_escala_hunt_w;
end;
