CREATE OR REPLACE FUNCTION obter_desc_escala_jcs ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);
ie_digit_grade_1_w escala_japan_coma.ie_digit_grade_1%type;
ie_digit_grade_2_w escala_japan_coma.ie_digit_grade_2%type;
ie_digit_grade_3_w escala_japan_coma.ie_digit_grade_3%type;
ie_restlesness_w  escala_japan_coma.ie_restlesness%type;
ie_incontinence_w  escala_japan_coma.ie_incontinence%type;

BEGIN

select ie_digit_grade_1,
       ie_digit_grade_2,
       ie_digit_grade_3,
       ie_restlesness,
       ie_incontinence
into STRICT   ie_digit_grade_1_w,
       ie_digit_grade_2_w,
       ie_digit_grade_3_w,
       ie_restlesness_w,
       ie_incontinence_w
from escala_japan_coma
where nr_sequencia = nr_sequencia_p;

if (ie_digit_grade_1_w IS NOT NULL AND ie_digit_grade_1_w::text <> '') then

		ds_retorno_w	:= ie_digit_grade_1_w;

elsif (ie_digit_grade_2_w IS NOT NULL AND ie_digit_grade_2_w::text <> '') then

		ds_retorno_w	:= ie_digit_grade_2_w;

elsif (ie_digit_grade_3_w IS NOT NULL AND ie_digit_grade_3_w::text <> '') then

		ds_retorno_w	:= ie_digit_grade_3_w;

end if;

if (ie_restlesness_w      = 'S') then
		ds_retorno_w := ds_retorno_w || 'R';
end if;
if (ie_incontinence_w      = 'S') then
		ds_retorno_w := ds_retorno_w || 'I';
end if;

return ds_retorno_w;

end;
