CREATE OR REPLACE FUNCTION obter_desc_escala_regra_gqa ( nr_sequencia_p bigint, nr_seq_flex_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_escala_flex_w		varchar(255);
ds_escala_w		varchar(255);


BEGIN

select  	substr(OBTER_DESCRICAO_PADRAO('VICE_ESCALA','DS_INFORMACAO',nr_sequencia_p),1,255)
into STRICT	ds_escala_w
;

if (nr_seq_flex_p IS NOT NULL AND nr_seq_flex_p::text <> '') then

	if (nr_sequencia_p = 126) then
		select	ds_escala
		into STRICT	ds_escala_flex_w
		from	eif_escala_ii
		where	nr_sequencia = nr_seq_flex_p;
	else
		select	ds_escala
		into STRICT	ds_escala_flex_w
		from	eif_escala
		where	nr_sequencia = nr_seq_flex_p;
	end if;

	ds_retorno_w := ds_escala_w||' ('||ds_escala_flex_w||')';
else

	ds_retorno_w := ds_escala_w;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
