elsif (qt_score > 10) then
  ds_retorno	:= 	obter_desc_expressao(756070, GET_SEQ_LANGUAGE_ESTABLISHMENT(wheb_usuario_pck.get_cd_estabelecimento));
end if;


return	ds_retorno;

end;
