CREATE OR REPLACE FUNCTION obter_desc_escala_sara (qt_pontuacao bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

if (qt_pontuacao IS NOT NULL AND qt_pontuacao::text <> '') then
	if (qt_pontuacao = 0) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(307938); -- Nenhuma lesão
	elsif (qt_pontuacao between 0.1 and 2.5) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(307939); -- Lesão leve a moderada
	elsif (qt_pontuacao > 2.5) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(307944); -- Lesão grave (SARA)
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
