CREATE OR REPLACE FUNCTION obter_desc_escala_shim (qt_pontuacao_p bigint,ie_tipo_retorno text) RETURNS varchar AS $body$
DECLARE


ds_classe_w 	varchar(60);
ds_retorno_w 	varchar(100);

BEGIN

if (qt_pontuacao_p = 21) then
	ds_classe_w := wheb_mensagem_pck.get_texto(317485); --'Disfunção erétil leve'
elsif (qt_pontuacao_p > 20) then
	ds_classe_w := wheb_mensagem_pck.get_texto(312659); --'Sem Disfunção erétil'
elsif (qt_pontuacao_p >= 5) then
    if (qt_pontuacao_p < 8) then
    	ds_classe_w := wheb_mensagem_pck.get_texto(317484); --'Disfunção erétil severa'
    elsif (qt_pontuacao_p < 12) then
    	ds_classe_w := wheb_mensagem_pck.get_texto(317483); --'Disfunção erétil moderada'
    elsif (qt_pontuacao_p < 17) then
    	ds_classe_w := wheb_mensagem_pck.get_texto(312658); --'Disfunção erétil leve a moderada'
    elsif (qt_pontuacao_p <= 20) then
    	ds_classe_w := wheb_mensagem_pck.get_texto(317485); --'Disfunção erétil leve'
    end if;
end if;

ds_retorno_w := ds_classe_w;

return ds_retorno_w;
end;
