CREATE OR REPLACE FUNCTION obter_desc_escala_stratify (qt_score_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE


/*
Opção
D			Descrição
R			Risco
*/
ds_retorno_w		varchar(255);


BEGIN

if (qt_score_p	>= 9 ) then
	if (ie_opcao_p = 'D') then
		ds_retorno_w	:=	Wheb_mensagem_pck.get_texto(321448);--'Presença de risco elevado de queda';
	else
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(277357); --'S';
	end if;
else
	if (ie_opcao_p = 'D') then
		ds_retorno_w	:=	Wheb_mensagem_pck.get_texto(321449); --'Ausência de risco elevado de queda';
	else
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(321450); --'N';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
