CREATE OR REPLACE FUNCTION obter_desc_escala_thompson (qt_score_p bigint) RETURNS varchar AS $body$
DECLARE


ds_score_w varchar(255) := '';


BEGIN

if (qt_score_p IS NOT NULL AND qt_score_p::text <> '') then
begin
	if ( qt_score_p >= 1 and qt_score_p <= 10 ) then
		ds_score_w := obter_desc_expressao(971769); -- mild hie
	elsif ( qt_score_p >= 11 and qt_score_p <= 14 ) then
		ds_score_w := obter_desc_expressao(971771); -- moderate hie
	elsif ( qt_score_p >= 15 ) then
		ds_score_w := obter_desc_expressao(971773); -- severe hie
	end if;
end;
end if;

return ds_score_w;
end;
