CREATE OR REPLACE FUNCTION obter_desc_escala_waterlow ( qt_score_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(100);

BEGIN
  IF (qt_score_p    >= 0 AND qt_score_p <= 10) THEN
    ds_retorno_w    := wheb_mensagem_pck.get_texto(1076552); -- Low risk
  elsif (qt_score_p >= 11 AND qt_score_p <= 15) THEN
    ds_retorno_w    := wheb_mensagem_pck.get_texto(1076553); -- At risk
  elsif (qt_score_p >= 16 AND qt_score_p <= 20) THEN
    ds_retorno_w    := wheb_mensagem_pck.get_texto(1076554); -- High risk
  elsif (qt_score_p >= 21 ) THEN
    ds_retorno_w    := wheb_mensagem_pck.get_texto(1076555); -- Very high risk
  END IF;
RETURN ds_retorno_w;
END;
