CREATE OR REPLACE FUNCTION obter_desc_espec_medica (cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_especialidade_w		varchar(100)	:= '';


BEGIN
if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then
	begin
	select substr(max(ds_especialidade),0,100)
	into STRICT ds_especialidade_w
	from especialidade_medica
	where cd_especialidade = cd_especialidade_p;
	end;
end if;
RETURN ds_especialidade_w;
END;
$body$
