CREATE OR REPLACE FUNCTION obter_desc_esquema_contabil (nr_seq_esquema_p bigint) RETURNS varchar AS $body$
DECLARE


ds_regra_esquema_prov_w		pls_esquema_contabil.ds_regra%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN

ds_regra_esquema_prov_w		:= '';

if (coalesce(nr_seq_esquema_p,0) > 0) then
	select	max(ds_regra)
	into STRICT	ds_regra_esquema_prov_w
	from	pls_esquema_contabil
	where	nr_sequencia = nr_seq_esquema_p;
end if;

return	ds_regra_esquema_prov_w;

end;
$body$
