CREATE OR REPLACE FUNCTION obter_desc_estab_adicional (ds_codigo_p text) RETURNS varchar AS $body$
DECLARE


lista_estab_w		lista_varchar_pck.tabela_varchar;

ds_codigo_w	varchar(2000);
ds_retorno_w	varchar(4000) := '';
ds_estab_w	varchar(100);

BEGIN
ds_codigo_w	:= ds_codigo_p;

if (ds_codigo_w IS NOT NULL AND ds_codigo_w::text <> '') then


	lista_estab_w := obter_lista_string2(ds_codigo_w, ',');

	for	i in lista_estab_w.first..lista_estab_w.last loop
			begin
			select	substr(obter_nome_estabelecimento((lista_estab_w(i))::numeric ),1,255)
			into STRICT	ds_estab_w
			;

			ds_retorno_w	:= ds_estab_w ||', '||ds_retorno_w;
			exception
			when others then
				ds_retorno_w := ds_retorno_w;
			end;
	end loop;

end if;


RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
