CREATE OR REPLACE FUNCTION obter_desc_estagio_entrega (nr_seq_estagio_entrega_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_estagio_w ESTAGIO_STATUS_ENTREGA.NR_SEQ_ESTAGIO%TYPE;
ds_retorno_w varchar(255);


BEGIN
ds_retorno_w := null;

if (nr_seq_estagio_entrega_p IS NOT NULL AND nr_seq_estagio_entrega_p::text <> '') then
	select nr_seq_estagio
	into STRICT nr_seq_estagio_w
	from estagio_status_entrega est_sta_ent
	where nr_sequencia = nr_seq_estagio_entrega_p;
end if;

if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
	select substr(ds_estagio,1,255)
	into STRICT ds_retorno_w
	from estagio_entrega
	where nr_sequencia = nr_seq_estagio_w;

end if;

return ds_retorno_w;

end;
$body$
