CREATE OR REPLACE FUNCTION obter_desc_estagio_regulacao ( nr_seq_estag_regulacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_estag_regulacao_p IS NOT NULL AND nr_seq_estag_regulacao_p::text <> '') then
	begin
	select  substr(ds_prioridade,1,255)
	into STRICT	ds_retorno_w
	from 	prioridade_regulacao
	where	nr_sequencia = nr_seq_estag_regulacao_p;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
