CREATE OR REPLACE FUNCTION obter_desc_estrut_loc_material (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_estrut_sup_w	bigint;
ds_estrutura_w		varchar(80);

BEGIN

select	ds_estrutura,
	coalesce(nr_seq_estrut_sup,0)
into STRICT	ds_estrutura_w,
	nr_seq_estrut_sup_w
from	mat_estrutura
where	nr_sequencia = nr_sequencia_p;

ds_retorno_w := ds_estrutura_w;

/*NIVEL 1*/

if (nr_seq_estrut_sup_w > 0) then

	select	a.ds_estrutura,
		coalesce(a.nr_seq_estrut_sup,0)
	into STRICT	ds_estrutura_w,
		nr_seq_estrut_sup_w
	from	mat_estrutura a
	where	a.nr_sequencia = nr_seq_estrut_sup_w;
	ds_retorno_w := substr(ds_estrutura_w || ' - ' || ds_retorno_w,1,255);

	/*NIVEL 2*/

	if (nr_seq_estrut_sup_w > 0) then

		select	a.ds_estrutura,
			coalesce(a.nr_seq_estrut_sup,0)
		into STRICT	ds_estrutura_w,
			nr_seq_estrut_sup_w
		from	mat_estrutura a
		where	a.nr_sequencia = nr_seq_estrut_sup_w;
		ds_retorno_w := substr(ds_estrutura_w || ' - ' || ds_retorno_w,1,255);

		/*NIVEL 3*/

		if (nr_seq_estrut_sup_w > 0) then

			select	a.ds_estrutura,
				coalesce(a.nr_seq_estrut_sup,0)
			into STRICT	ds_estrutura_w,
				nr_seq_estrut_sup_w
			from	mat_estrutura a
			where	a.nr_sequencia = nr_seq_estrut_sup_w;
			ds_retorno_w := substr(ds_estrutura_w || ' - ' || ds_retorno_w,1,255);

			/*NIVEL 4*/

			if (nr_seq_estrut_sup_w > 0) then

				select	a.ds_estrutura,
					coalesce(a.nr_seq_estrut_sup,0)
				into STRICT	ds_estrutura_w,
					nr_seq_estrut_sup_w
				from	mat_estrutura a
				where	a.nr_sequencia = nr_seq_estrut_sup_w;
				ds_retorno_w := substr(ds_estrutura_w || ' - ' || ds_retorno_w,1,255);

				/*NIVEL 5*/

				if (nr_seq_estrut_sup_w > 0) then

					select	a.ds_estrutura,
						coalesce(a.nr_seq_estrut_sup,0)
					into STRICT	ds_estrutura_w,
						nr_seq_estrut_sup_w
					from	mat_estrutura a
					where	a.nr_sequencia = nr_seq_estrut_sup_w;
					ds_retorno_w := substr(ds_estrutura_w || ' - ' || ds_retorno_w,1,255);

				end if;
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
