CREATE OR REPLACE FUNCTION obter_desc_estrut_mat_regra ( cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	substr(CASE WHEN ie_situacao='I' THEN 'INATIVO - '  ELSE '' END  || ds_material || ' (' || cd_material || ')',1,255)
	into STRICT	ds_retorno_w
	from	material
	where	cd_material	= cd_material_p;
	end;
elsif (cd_classe_material_p IS NOT NULL AND cd_classe_material_p::text <> '') then
	begin
	select	substr(CASE WHEN ie_situacao='I' THEN 'INATIVO - '  ELSE '' END  || ds_classe_material || ' (' || cd_classe_material || ')',1,255)
	into STRICT	ds_retorno_w
	from	classe_material
	where	cd_classe_material	= cd_classe_material_p;
	end;
elsif (cd_subgrupo_material_p IS NOT NULL AND cd_subgrupo_material_p::text <> '') then
	begin
	select	substr(CASE WHEN ie_situacao='I' THEN 'INATIVO - '  ELSE '' END  || ds_subgrupo_material || ' (' || cd_subgrupo_material || ')',1,255)
		ds_subgrupo_material
	into STRICT	ds_retorno_w
	from	subgrupo_material
	where	cd_subgrupo_material	= cd_subgrupo_material_p;
	end;
elsif (cd_grupo_material_p IS NOT NULL AND cd_grupo_material_p::text <> '') then
	begin
	select	distinct substr(CASE WHEN ie_situacao='I' THEN 'INATIVO - '  ELSE '' END  || ds_grupo_material || ' (' || cd_grupo_material || ')',1,255)
	into STRICT	ds_retorno_w
	from	grupo_material
	where	cd_grupo_material	= cd_grupo_material_p;
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
