CREATE OR REPLACE FUNCTION obter_desc_etapas_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_atual_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_turno_w 	varchar(1000);
ds_retorno_w	varchar(4000);
nr_etapa_w	smallint;
dt_suspensao_w	timestamp;

C01 CURSOR FOR
SELECT	distinct
	substr(ds_turno,position('das'  ds_turno),30),
	nr_etapa_sol,
	dt_suspensao
from	prescr_mat_hor
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_solucao	= nr_seq_solucao_p
and	dt_horario between dt_inicial_p and dt_final_p
order by nr_etapa_sol;


BEGIN

open C01;
loop
fetch C01 into
	ds_turno_w,
	nr_etapa_w,
	dt_suspensao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
		ds_turno_w := ds_turno_w || ' S';
	elsif (nr_etapa_w <= nr_etapa_atual_p) then
		ds_turno_w := ds_turno_w || ' A';
	end if;

	ds_retorno_w := ds_retorno_w || ds_turno_w || ';' || chr(13) || chr(10);

end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
