CREATE OR REPLACE FUNCTION obter_desc_exame_lab ( nr_seq_exame_p bigint, cd_exame_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_exame_P IS NOT NULL AND nr_seq_exame_P::text <> '') then
	select substr(nm_exame,1,255)
	into STRICT	 ds_retorno_w
	from	 exame_laboratorio
	where	 nr_seq_exame	= nr_seq_exame_p;
elsif (cd_exame_p IS NOT NULL AND cd_exame_p::text <> '') then
	select substr(nm_exame,1,255)
	into STRICT	 ds_retorno_w
	from	 exame_laboratorio
	where	 cd_exame   = cd_exame_p;
elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select substr(ds_procedimento,1,255)
	into STRICT ds_retorno_w
	from procedimento
	where cd_procedimento	= cd_procedimento_p
	  and ie_origem_proced	= ie_origem_proced_p;
end if;

RETURN ds_retorno_w;
END;
