SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_exame_proc ( nr_prescricao_p prescr_procedimento.nr_prescricao%type, nr_seq_prescricao_p prescr_procedimento.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_prescricao_p,0) > 0) then
	
	begin

		select	Obter_regra_exame_status(pr.ie_status_atend) ds_status -- exames laboratoriais
		into STRICT	ds_retorno_w
		from	prescr_procedimento pr ,
                        exame_laboratorio ex 
		where	(pr.nr_seq_exame IS NOT NULL AND pr.nr_seq_exame::text <> '')
		and	pr.nr_prescricao = nr_prescricao_p
		and	pr.nr_sequencia = nr_seq_prescricao_p
                and	pr.nr_seq_exame = ex.nr_seq_exame
		and	coalesce(ex.ie_anatomia_patologica,'N') = 'N'
		
union 	all
 
		PERFORM	obter_valor_dominio(1226,ie_status_execucao) ds_status -- exames de imagem
		from	prescr_procedimento
		where	coalesce(nr_seq_exame::text, '') = ''
		and	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_prescricao_p
		
union all

		SELECT	obter_valor_dominio(1226,ie_status_execucao) ds_status -- exames de imagem
		from 	prescr_procedimento	pr,
			exame_laboratorio	ex
		where	(pr.nr_seq_lab IS NOT NULL AND pr.nr_seq_lab::text <> '')
		and	pr.nr_seq_exame = ex.nr_seq_exame
		and	coalesce(ex.ie_anatomia_patologica,'N') = 'S'
		and	pr.nr_prescricao = nr_prescricao_p
		and	pr.nr_sequencia = nr_seq_prescricao_p;
		
	exception
	when others then
		ds_retorno_w := '';
	end;
			
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
