CREATE OR REPLACE FUNCTION obter_desc_expressao_br ( cd_expressao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	dic_expressao.ds_expressao_br%type := null;

BEGIN


if (cd_expressao_p IS NOT NULL AND cd_expressao_p::text <> '') then
	begin
		begin
			select	ds_expressao_br
			into STRICT	ds_retorno_w
			from	dic_expressao
			where	cd_expressao	= cd_expressao_p;
		exception when no_data_found then
			--Deve existir uma express√£o cadastrada(SEMPRE), senao reportar
			CALL wheb_mensagem_pck.exibir_mensagem_abort(290409, 'CD_EXPRESSAO='||cd_expressao_p);
		end;
	end;
end if;

return	ds_retorno_w;

end;
